import React ,{useState} from 'react'
import Sidebar from '../components/sidebar/Sidebar'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
export const UpdateAdminPass = (props) => {

  const url = "http://localhost:5000/api/admins/updateadminpass"
    const [data, setData] = useState({
        email: "",
        pass: ""
    })
    const [response, setResponse] = useState({
        data: "",
        status: ""

    })
    function handle(e) {
        // e.preventDefault()  ;
        console.log(e.target.value)
        console.log(e.target.id)

        const newdata = { ...data }
        newdata[e.target.id] = e.target.value
        setData(newdata)



    }


    const submit = async (e) => {
        e.preventDefault();
        try {

            await axios.post(url, data)
                .then((res) => res)
                .then((res) => {

                    response.status = res.status
                    response.data = res.data

                    if ( response.status == "200") {
                        toast(response.data, {
                            position: "bottom-right",
                            autoClose: 5000,
                            hideProgressBar: false,
                            closeOnClick: true,
                            pauseOnHover: true,
                            draggable: true,
                            progress: undefined,
                        });
                        setTimeout(() => {
                            checkislogin(e)
                        }, 5200)
                    }
                    else {
                        console.log(response.data)
                        toast(response.data, {
                            position: "bottom-right",
                            autoClose: 5000,
                            hideProgressBar: false,
                            closeOnClick: true,
                            pauseOnHover: true,
                            draggable: true,
                            progress: undefined,
                        });
                    }

                })
        }
        catch (err) {
            toast(err.response.data, {
                position: "bottom-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }
        console.log(response.status)



    }
    function checkislogin(e) {
        e.preventDefault();

        // alert("login succesful")
        console.log(response.status)
        window.location.href = "http://localhost:3000/dashboard"
        window.history(1);
        // }
        // else
        { console.log("in else") }
    }

    

  return (
    <div>
     <Sidebar {...props}/>

      <h2 className="page-header">Admin</h2>
      <hr className="featurette-divider" />
      <h4 className="page-header">Update Password</h4>
      <div className="container">
        <form>
          <div className="col-md-6">
            <label htmlFor="inputEmail4" className="form-label">
              Password
            </label>
            <input type="password" name="password" className="form-control" id="inputEmail4" />
          </div>
          <div className="col-6">
            <label htmlFor="inputEmail4" className="form-label">
              Confirm Password
            </label>
            <input type="password" name="confirmPassword" className="form-control" id="inputEmail4" />
          </div>
          <div className="col-12 my-4">

            <button type="submit" className="btn btn-primary">
              Update Password
            </button>

          </div>
        </form>
      </div>
    </div>
)}
